(function (results, result, site) {
  var items = $(result).find('item');
  $.each(items, function (index, item) {
    item = $(item);
    var data = {
      title: item.find("title").text(),
      link: item.find("link").text(),
      url: item.find("enclosure").attr("url") + (site && site.passkey?"&passkey="+site.passkey:""),
      size: item.find("enclosure").attr("length"),
      date: (new Date(item.find("pubDate").text())).toLocaleString(),
      author: item.find("author").text()
    };
    results.push(data);
  });
  console.log(results);
})(results, result, options.site)
